mapper.insert:

INSERT INTO ISY_ORDER 
	( ORDER_CODE
	, ORDER_NAME
	, ORDER_STATE
	, REGIST_ID
	, REGIST_DT
	, PK_PRODUCT_SEQ
	, PK_USER_SEQ
	)
VALUES 
	( #{orderCode}
	  , #{orderName}
	  , #{orderState}
	  , #{registId}
	  , SYSDATE()
	  , #{pkProductSeq}
	  , #{pkUserSeq}
	  )

mapper.update:

	UPDATE ISY_ORDER SET
		ORDER_CODE = #{orderCode}
		  , ORDER_NAME = #{orderName}
		  , ORDER_STATE = #{orderState}
	WHERE PK_ORDER_SEQ = #{pkOrderSeq}
