mapper.insert:

INSERT INTO ISY_PAY 
	( PAY_CODE
	, PAY_METHOD
	, PAY_STATE
	, REGIST_ID
	, REGIST_DT
	, PK_PRODUCT_SEQ
	, PK_USER_SEQ
	   )
VALUES 
	( #{payCode}
	  , #{payMethod}
	  , #{payState}
	  , #{registId}
	  , SYSDATE()
	  , #{pkProductSeq}
	  , #{pkUserSeq}
	  )

mapper.update:

	UPDATE ISY_PAY SET
		PAY_CODE = #{payCode}
	  , PAY_METHOD = #{payMethod}
	  , PAY_STATE = #{payState}
	WHERE PK_PAY_SEQ = #{pkPaySeq}
